rotateMatrix = [cos(weaponTheta)*cos(weaponPhi) cos(weaponTheta)*sin(weaponPhi)  -sin(weaponTheta);
    sin(weaponGamma)*sin(weaponTheta)*cos(weaponPhi)-cos(weaponGamma)*sin(weaponPhi)  sin(weaponGamma)*sin(weaponTheta)*sin(weaponPhi)+cos(weaponGamma)*cos(weaponPhi)  sin(weaponGamma)*cos(weaponTheta)
    cos(weaponGamma)*sin(weaponTheta)*cos(weaponPhi)+sin(weaponGamma)*sin(weaponPhi)  cos(weaponGamma)*sin(weaponTheta)*sin(weaponPhi)-sin(weaponGamma)*cos(weaponPhi)   cos(weaponGamma)*cos(weaponTheta)];

vertices = rotateMatrix * vertices * magnifyMultiple + [weaponX,weaponY,weaponZ]';

trimesh(faces', vertices(1,:), vertices(2,:), vertices(3,:),'FaceColor',weaponColor,'EdgeColor',weaponColor);
axis equal;
hold on;

end

